mapper.update:
    	UPDATE student SET 
    		  name = #{name}
			  ,sex = #{sex}
			  ,tel = #{tel}
			  ,qqNum = #{qqNum}
			  ,province = #{province}
			  ,city = #{city}
			  ,area = #{area}
			  ,school = #{school}
			  ,college = #{college}
			  ,major = #{major}
			  ,grade = #{grade}
			  ,classes = #{classes}
			  ,position = #{position}
		WHERE id = #{id}

mapper.select:

      SELECT * FROM student 
      
      
      	province = #{province}
      
      
      	AND city = #{city}
      
      
      	AND area = #{area}
      
      
      	AND school = #{school}
      
      
      	AND college = #{college}
      
      
      	AND major = #{major}
      
      
      	AND grade = #{grade}
      
      
      	AND classes = #{classes}
      
      
      	AND position = #{position}
      
      
      	AND name = #{name}
      
      
      	AND tel = #{tel}
      
      
      LIMIT #{start},#{len}


      SELECT count(1) FROM student 
      
      
      	province = #{province}
      
      
      	AND city = #{city}
      
      
      	AND area = #{area}
      
      
      	AND school = #{school}
      
      
      	AND college = #{college}
      
      
      	AND major = #{major}
      
      
      	AND grade = #{grade}
      
      
      	AND classes = #{classes}
      
      
      	AND position = #{position}
      
      
      	AND name = #{name}
      
      
      	AND tel = #{tel}
